INSERT INTO `menu` VALUES (6, 'هیئت امنا', 'مدیریت', '2ece248c-ff87-4091-8ebe-e4fa961a881e.jpg', 'هیات امنا عالی ترین ركن دانشگاه می باشد كه وظایف و اختیارات آن در قانون تشكیل هیأت های امنای دانشگاه ها و مؤسسات آموزش عالی و پژوهشی (مصوب جلسات 181 و 183 مورخ 1367/12/9&nbsp; شورای عالی انقلاب فرهنگی) و همچنین قانون نحوه انجام امور مالی و معاملاتی دانشگاه ها و مؤسسات آموزش عالی و تحقیقاتی( مصوب مورخ 1369/10/18مجلس شورای اسلامی) تعیین شده است.<br>');
