INSERT INTO `menu` VALUES (13, 'امور آموزشی', 'آموزش', '2ece248c-ff87-4091-8ebe-e4fa961a881e.jpg', '<div><br></div><div><table id=\"wfa7bf8a29cb477e62f7ac0ca1b870ad60\" class=\"table table-striped table-bordered detail-view\" style=\"width: 817.5px; margin-bottom: 20px; color: rgb(51, 51, 51); border-color: rgb(221, 221, 221); border-spacing: 0px; max-width: 100%; font-family: IRANSans, tahoma; font-size: 14px;\"><tbody><tr style=\"background-color: rgb(249, 249, 249);\"><th style=\"text-align: right; padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">تلفن</th><td style=\"padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">081-35455145</td></tr><tr><th style=\"text-align: right; padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">دورنگار</th><td style=\"padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">081-35345147</td></tr><tr style=\"background-color: rgb(249, 249, 249);\"><th style=\"text-align: right; padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">صندوق پستی</th><td style=\"padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">6553139771<br></td></tr></tbody></table></div><p dir=\"rtl\" style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px;\"><b><font color=\"#333333\" face=\"IRANSans, tahoma\"><span style=\"font-size: 14px;\">شرح وظایف:</span></font></b></p><p dir=\"rtl\" style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px;\"></p><ul><li><font color=\"#333333\" face=\"IRANSans, tahoma\"><span style=\"font-size: 14px;\">تهیه و پیشنهاد کلیه آیین نامه‌های آموزشی، بخشنامه‌ها و دستورالعمل‌های آموزشی، دانش‌آموختگی و ابلاغ آن به واحدهای استانی.</span></font></li><li><font color=\"#333333\" face=\"IRANSans, tahoma\"><span style=\"font-size: 14px;\">&nbsp;بهبود و اصلاح فرآیندهای آموزشی و دانش‌آموختگی در راستای افزایش کیفیت آموزش‌های مهارت محور.</span></font></li><li><font color=\"#333333\" face=\"IRANSans, tahoma\"><span style=\"font-size: 14px;\">&nbsp;تدوین ملاک‌ها، شاخص‌ها و استانداردهای آموزشی و دانش‌آموختگی از طریق بازبینی و بهبود اصلاح فرایندها.</span></font></li><li><font color=\"#333333\" face=\"IRANSans, tahoma\"><span style=\"font-size: 14px;\">&nbsp;هم‌سو نمودن فعالیتهای آموزشی واحدهای استانی تحت نظارت با راهبردهای و سیاستهای کلان دانشگاه.</span></font></li><li><font color=\"#333333\" face=\"IRANSans, tahoma\"><span style=\"font-size: 14px;\">&nbsp;نظارت بر عملکرد آموزش واحدهای استانی.</span></font></li><li><font color=\"#333333\" face=\"IRANSans, tahoma\"><span style=\"font-size: 14px;\">&nbsp;پاسخگویی و تکریم ارباب رجوع جهت رفع مشکلات آموزشی و دانش‌آموختگی.</span></font></li><li><font color=\"#333333\" face=\"IRANSans, tahoma\"><span style=\"font-size: 14px;\">&nbsp;برنامه‌ریزی و تنظیم تقویم آموزشی و نظارت بر حسن اجرای آن.</span></font></li><li><font color=\"#333333\" face=\"IRANSans, tahoma\"><span style=\"font-size: 14px;\">&nbsp;پیاده سازی مقررات آموزشی در سامانه جامع آموزشی دانشگاه.</span></font></li><li><font color=\"#333333\" face=\"IRANSans, tahoma\"><span style=\"font-size: 14px;\">&nbsp;تشکیل مرتب و منظم شورای تخصصی آموزشی دانشگاه و شورای بررسی موارد خاص دانشگاه.</span></font></li><li><font color=\"#333333\" face=\"IRANSans, tahoma\"><span style=\"font-size: 14px;\">&nbsp;پاسخگویی مکتوب، دقیق و به موقع کارشناسانه به تمام درخواستها و مکاتبات ارجاعی.</span></font></li><li><font color=\"#333333\" face=\"IRANSans, tahoma\"><span style=\"font-size: 14px;\">&nbsp;صدوراحکام روسای شورای آموزشی و کمیسیون موارد خاص استانی.</span></font></li><li><font color=\"#333333\" face=\"IRANSans, tahoma\"><span style=\"font-size: 14px;\">&nbsp;تهیه و تنظیم بودجه آموزشی و دانش‌آموختگی استانها.</span></font></li><li><font color=\"#333333\" face=\"IRANSans, tahoma\"><span style=\"font-size: 14px;\">&nbsp;برگزاری کارگاه آموزشی ویژه واحدهای استانی، روسای مراکز و کارشناسان آموزش و دانش‌آموختگان.</span></font></li><li><font color=\"#333333\" face=\"IRANSans, tahoma\"><span style=\"font-size: 14px;\">&nbsp;صدور گواهی اشتغال به تحصیل جهت تمدید روادید ویزای تحصیلی دانشجویان غیر ایرانی.</span></font></li><li><font color=\"#333333\" face=\"IRANSans, tahoma\"><span style=\"font-size: 14px;\">&nbsp;تعامل با سازمان وظیفه عمومی در جهت بهبود فرایندهای صدور معافیت تحصیلی دانشجویان مشمول متقاضی خروج از کشور، دریافت سنوات تحصیلی ارفاقی برای دانشجویان مشمول.</span></font></li><li><font color=\"#333333\" face=\"IRANSans, tahoma\"><span style=\"font-size: 14px;\">&nbsp;نظارت برحسن اجرای دروس عملی و یا مهارتی به ویژه دروس کاربینی و کارورزی.</span></font></li><li><font color=\"#333333\" face=\"IRANSans, tahoma\"><span style=\"font-size: 14px;\">&nbsp;طراحی و برنامه‌ریزی در جهت اجرای روش های جدید صدور مدارک با تکیه بر فناوری اطلاعات.</span></font></li><li><font color=\"#333333\" face=\"IRANSans, tahoma\"><span style=\"font-size: 14px;\">&nbsp;تعیین نوع مدارک به دانش‌آموختگان متناسب مقرارت آموزشی.</span></font></li><li><font color=\"#333333\" face=\"IRANSans, tahoma\"><span style=\"font-size: 14px;\">&nbsp;بررسی و سیاست گزاری درخصوص شیوه‌های دانش‌آموختگی دانشجویان متمرکز، نیمه متمرکز، دوره های آموزش خاص و غیرحضوری، پودمان ها، تک درس ها.</span></font></li><li><font color=\"#333333\" face=\"IRANSans, tahoma\"><span style=\"font-size: 14px;\">&nbsp;برنامه‌ریزی جهت هماهنگی درون بخشی معاونت‌های مختلف ذینفع دانشگاه.</span></font></li><li><font color=\"#333333\" face=\"IRANSans, tahoma\"><span style=\"font-size: 14px;\">&nbsp;تهیه و تنظیم آیتم های موجود در فرآیند دانش‌آموختگی.</span></font></li><li><font color=\"#333333\" face=\"IRANSans, tahoma\"><span style=\"font-size: 14px;\">&nbsp;تأیید ریزنمرات و مدارک قابل ترجمه دانش‌آموختگان متناسب درخواست افراد ذینفع.</span></font></li><li><font color=\"#333333\" face=\"IRANSans, tahoma\"><span style=\"font-size: 14px;\">&nbsp;صدور تفویض اختیار امضاء گواهینامه موقت دانش‌آموختگان و تائیدیه تحصیلی به روسا/سرپرستان واحدهای استانی از طرف معاونت آموزشی دانشگاه.</span></font></li><li><font color=\"#333333\" face=\"IRANSans, tahoma\"><span style=\"font-size: 14px;\">&nbsp;نظارت بر نحوه بررسی و تائید پرونده دانش‌آموختگان.</span></font></li><li><font color=\"#333333\" face=\"IRANSans, tahoma\"><span style=\"font-size: 14px;\">&nbsp;بروز رسانی مدارک صادره دانشگاه (دانشنامه).</span></font></li><li><font color=\"#333333\" face=\"IRANSans, tahoma\"><span style=\"font-size: 14px;\">&nbsp;تهیه و تنظیم آمار دانش آموختگان.</span></font></li></ul><p></p><p dir=\"rtl\" style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px;\"></p>');
INSERT INTO `menu` VALUES (14, 'امور تحصیلات تکمیلی', 'آموزش', '2ece248c-ff87-4091-8ebe-e4fa961a881e.jpg', '<div><table id=\"wfa7bf8a29cb477e62f7ac0ca1b870ad60\" class=\"table table-striped table-bordered detail-view\" style=\"width: 817.5px; margin-bottom: 20px; color: rgb(51, 51, 51); border-color: rgb(221, 221, 221); border-spacing: 0px; max-width: 100%; font-family: IRANSans, tahoma; font-size: 14px;\"><tbody><tr style=\"background-color: rgb(249, 249, 249);\"><th style=\"text-align: right; padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">تلفن</th><td style=\"padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">081-35455145</td></tr><tr><th style=\"text-align: right; padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">دورنگار</th><td style=\"padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">081-35345147</td></tr><tr style=\"background-color: rgb(249, 249, 249);\"><th style=\"text-align: right; padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">صندوق پستی</th><td style=\"padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">6553139771</td></tr></tbody></table></div><span style=\"font-weight: bolder;\">شرح وظایف:</span><div><div style=\"\"><ul><li>دبیری شورای آموزشی دانشگاه و تنظیم و تدوین مصوبات این شورا</li><li>تهیه، تنظیم و نگهداری برنامه های مصوب آموزشی دانشجویان و تدوین مقررات مربوط به صورت کاتولوگ های معرفی دوره ها</li><li>تهیه و ابلاغ تقویم دانشگاه و نظارت بر تنظیم برنامه های هفتگی و امتحانی دروس</li><li>نظارت بر حسن اجرای آ یین نامه ها، بخشنامه ها و مصوبات آموزشی در واحدهای ذی ربط و نظارت بر اداره خدمات آموزشی در واحدهای تابعه</li><li>رسیدگی به مشکلات دانشجویان و برآورد نارسایی های آئین نامه ها و مصوبات و پیشنهاد راه حل به معاونت آموزشی و تحصیلات تکمیلی دانشگاه</li><li>برگزاری آزمون ها، امتحانات ورودی دانشگاه ها</li><li>تهیه و استفاده از آمار و اطلاعات</li><li>انجام امور ثبت نام و امتحانات دانشجویان کلیه مقاطع تحصیلی</li><li>دریافت لیست پذیرفته شدگان کلیه مقاطع تحصیلی از سازمان سنجش آموزش کشور</li><li>اعمال دستورالعمل های صادره در خصوص ثبت نام پذیرفته شدگان کلیه مقاطع تحصیلی در هر ترم</li><li>همکاری با دانشکده ها در تهیه برنامه های هفتگی و ارسال آن به مرکز فناوری اطلاعات و ارتباطات</li><li>انجام امور مربوط به امور پذیرفته شدگان کلیه مقاطع تحصیلی</li><li>پاسخ گویی به سؤالات دانشجویان در زمینه امور آموزشی</li><li>انجام امور مربوط به صدور گواهی، معرفی نامه و سایر مکاتبات کلیه مقاطع تحصیلی</li><li>ا بلاغ برنامه های امتحانی برطبق تصمیمات متخذه به گروه های آموزشی و نظارت بر امر امتحانات پایان ترم</li><li>انجام امور مربوط به تطبیق واحدها و راهنمایی دانشجویان در زمینه انتخاب دروس</li><li>صدور دانشنامه و گواهی های موقت کلیه مقاطع تحصیلی دانش آموختگان</li><li>انجام امور مربوط به دانش آموختگان</li><li>انجام امور مربوط به کمسیون موارد خاص دانشگاهی و استانی</li><li>ارسال تأییدیه فارغ التحصیلی کلیه مقاطع تحصیلی دانشجویان به دانشگاه ها و مراکز دولتی</li><li>حفظ و نگهداری سوابق تحصیلی دانش آموختگان کلیه مقاطع تحصیلی</li><li>انجام امور مربوط به نظام وظیفه پذیرفته شدگان کلیه مقاطع تحصیلی</li><li>دریافت فهرست نمرات و حضور و غیاب و ارسال آن به واحد رایانه و تهیه کارنامه تحصیلی دانشجویان</li><li>انجام سایر امور محوله از سوی مقام مافوق</li></ul></div><div><span style=\"font-weight: bolder;\"><br></span></div></div>');
INSERT INTO `menu` VALUES (15, 'آموزش بلند مدت', 'آموزش', '2ece248c-ff87-4091-8ebe-e4fa961a881e.jpg', '<div style=\"text-align: justify;\"><b><span style=\"text-align: left; color: rgb(51, 51, 51); font-family: IRANSans, tahoma; font-size: 14px;\">آیین‌نامه، بخش‌نامه و استاندارد</span><br></b></div><div><span style=\"text-align: left;\"><br></span></div><div class=\"content\" style=\"color: rgb(51, 51, 51); font-family: IRANSans, tahoma; font-size: 14px; margin: 20px 0px;\"><ul style=\"margin-bottom: 10px;\"><li><a href=\"https://dec.uast.ac.ir/file/download/page/1498368249-.pdf\" style=\"color: rgb(49, 93, 168);\">استانداردهای آموزشی مراکز آموزش&nbsp;</a><a href=\"https://dec.uast.ac.ir/file/download/page/1550317523-.pdf\" style=\"color: rgb(49, 93, 168);\">علمی - کاربردی</a></li><li><a href=\"https://dec.uast.ac.ir/file/download/page/1550317523-.pdf\" style=\"color: rgb(49, 93, 168);\">آیین‌نامه صدور مجوز دوره‌های علمی - کاربردی</a></li></ul></div>');
INSERT INTO `menu` VALUES (16, 'آموزش کوتاه مدت', 'آموزش', '2ece248c-ff87-4091-8ebe-e4fa961a881e.jpg', 'دانلود دستورالعمل اجرایی دوره های آموزش علی کوتاه مدت علمی کاربردی<div style=\"text-align: center;\"><a href=\"https://sto.uast.ac.ir/file/download/page/1603261711-dastoorolamal.pdf\" title=\"کوتاه مدت\" target=\"_blank\">دانلود نسخه pdf</a></div>');
