INSERT INTO `menu` VALUES (40, 'صندوق رفاه دانشجویان', 'دانشجویی', '78df7a37-573f-40d2-94aa-9c886c14e35e.jpg', '<p style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: justify;\"><font color=\"#000000\" face=\"dana-regular, Tahoma, Arial\"><span style=\"font-size: 13px;\">صندوق رفاه دانشجویان، یک موسسه مالی است که زیر مجموعه وزارت علوم، تحقیقات و فناوری فعالیت می کند.&nbsp; این صندوق وظیفه دارد تا در ایام تحصیلی دانشجویان اقدام به ارائه تسهیلات مالی به ایشان کند.</span></font></p><p style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: justify;\"><font color=\"#000000\" face=\"dana-regular, Tahoma, Arial\"><span style=\"font-size: 13px;\">صندوق رفاه دانشجویی در سه مقاطع مختلف تحصیلی و دانشگاه های مختلف اقدام به پرداخت کمک هزینه تحصیلی به دانشجویان دوره های مختلف می کند.</span></font></p><p style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: justify;\"><font color=\"#000000\" face=\"dana-regular, Tahoma, Arial\"><span style=\"font-size: 13px;\">بازپرداخت این وام ها بدون سود و 5 ساله است.</span></font></p><p style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: justify;\"><font color=\"#000000\" face=\"dana-regular, Tahoma, Arial\"><span style=\"font-size: 13px;\">همچنین دانشجویان ملزم هستند برای دریافت این تسهیلات اقدام به ارائه یک تا دو سند ضمانت که در دفترخانه های ثبت اسناد رسمی ثبت می شود، کنند.</span></font></p><p style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: justify;\"><font color=\"#000000\" face=\"dana-regular, Tahoma, Arial\"><span style=\"font-size: 13px;\"><br></span></font></p><p style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px; text-align: justify;\"><font color=\"#000000\" face=\"dana-regular, Tahoma, Arial\"><span style=\"font-size: 13px;\">برای کسب اطلاعات بیشتر می‌توانید به وبگاه این صندوق از طریق لینک زیر مراجعه فرمایید:</span></font></p><p style=\"text-align: center; margin-right: 0px; margin-bottom: 10px; margin-left: 0px;\"><font color=\"#000000\" face=\"dana-regular, Tahoma, Arial\"><span style=\"font-size: 13px;\"><a href=\"https://www.swf.ir\" title=\"صندوق رفاه\" target=\"_blank\">https://www.swf.ir</a></span><br></font></p>');
INSERT INTO `menu` VALUES (41, 'امور اداری', 'اداری', '2ece248c-ff87-4091-8ebe-e4fa961a881e.jpg', '<div style=\"font-family: IRANSans;\"><div><table id=\"wfa7bf8a29cb477e62f7ac0ca1b870ad60\" class=\"table table-striped table-bordered detail-view\" style=\"width: 817.5px; margin-bottom: 20px; color: rgb(51, 51, 51); border-color: rgb(221, 221, 221); border-spacing: 0px; max-width: 100%; font-family: IRANSans, tahoma; font-size: 14px;\"><tbody><tr style=\"background-color: rgb(249, 249, 249);\"><th style=\"text-align: right; padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">تلفن</th><td style=\"padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">081-35455145</td></tr><tr><th style=\"text-align: right; padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">دورنگار</th><td style=\"padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">081-35345147</td></tr><tr style=\"background-color: rgb(249, 249, 249);\"><th style=\"text-align: right; padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">صندوق پستی</th><td style=\"padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">6553139771<br></td></tr></tbody></table></div><p dir=\"rtl\" style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px;\"><span style=\"font-weight: bolder;\"><font color=\"#333333\" face=\"IRANSans, tahoma\"><span style=\"font-size: 14px;\">شرح وظایف:</span></font></span></p></div><div><div><ul><li>اجراي سياستها و خط مشي هاي دانشگاه در زمينه هاي مربوط به امور اداري ،استخدامي و پشتيباني .</li><li>نظارت بر حسن انجام فعاليتهاي واحدهاي تابعه .</li><li>تهيه و تنظيم آيين نامه ها ، بخشنامه ها و دستور العملهاي اداري لازم جهت ارائه به مقامات ذيربط بر اساس ضوابط ومصوبات دانشگاه و ابلاغ آن به كليه واحدها پس از تاييد .</li><li>مراقبت در اجراي مقررات انضباطي و رسيدگي به تخلفات كاركنان و ارجاع مراتب به هيات هاي رسيدگي به تخلفات اداري .</li><li>مطالعه و بررسي پست های سازمانی و ارائه نظرات و پيشنهادات اصلاحي به مراجع ذيربط با همكاري مديريت طرح ،برنامه و بودجه</li><li>ايجاد ونگهداري بانك اطلاعاتي پرسنلي و سوابق خدمتي و اداري كليه پرسنل دانشگاه .</li><li>برنامه ريزي در جهت تامين نيروي انساني مورد نياز دانشگاه و ارائه طرح ها و پيشنهاد هاي لازم و همچنين انجام امور مربوط به استخدام آنها اعم از هيات علمي و غير هيات علمي .</li><li>پيش بيني ترفيع و تغيير گروه سالانه كاركنان با كمك واحدهاي مربوط .</li><li>نظارت در انجام كليه امور خدماتي دانشگاه و ارائه آن به واحدهاي مختلف .</li><li>انجام امور مربوط به قراردادهاي دانشگاه با هماهنگي مسئولين ذيربط .</li><li>تهيه طرحهاي مربوط به رفاه عمومي كاركنان و نظارت بر حسن اجراي طرحهاي مزبور .</li><li>همكاري در امر تهيه، تنظيم و اصلاح مجموعه شرح وظايف، نمودار و پستهاي سازماني دانشگاه با همکاری مديريت طرح، برنامه و بودجه&nbsp;</li><li>نظارت بر امور حضور و غياب، ورود و خروج و انتظامات دانشگاه و طرح تمام وقت كاركنان، اضافه كاري و انتصاب كاركنان&nbsp;</li><li>مشاركت در جلسات و كميسيونهاي مختلف و اضهار نظر و ارائه پيشنهادات لازم.&nbsp;</li><li>رسيدگي به امور رفاهي و بازنشستگي كاركنان و اتخاذ تصميمات لازم.</li><li>نظارت بر امر ارزشيابي واحدهاي تابعه .</li><li>انجام تداركات و تامين وسايل و تجهيزات مورد نياز واحد هاي مختلف دانشگاه و سفارش آن از داخل و خارج كشور با هماهنگي كامل واحدهاي ذيربط</li></ul></div></div>');
INSERT INTO `menu` VALUES (42, 'امور شهریه', 'اداری', '2ece248c-ff87-4091-8ebe-e4fa961a881e.jpg', '<div style=\"text-align: right; font-family: IRANSans;\"><div><table id=\"wfa7bf8a29cb477e62f7ac0ca1b870ad60\" class=\"table table-striped table-bordered detail-view\" style=\"width: 817.5px; margin-bottom: 20px; color: rgb(51, 51, 51); border-color: rgb(221, 221, 221); border-spacing: 0px; max-width: 100%; font-family: IRANSans, tahoma; font-size: 14px;\"><tbody><tr style=\"background-color: rgb(249, 249, 249);\"><th style=\"text-align: right; padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">تلفن</th><td style=\"padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">081-35455145</td></tr><tr><th style=\"text-align: right; padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">دورنگار</th><td style=\"padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">081-35345147</td></tr><tr style=\"background-color: rgb(249, 249, 249);\"><th style=\"text-align: right; padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">صندوق پستی</th><td style=\"padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">6553139771<br></td></tr></tbody></table></div><p dir=\"rtl\" style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px;\"><span style=\"font-weight: bolder;\"><font color=\"#333333\" face=\"IRANSans, tahoma\"><span style=\"font-size: 14px;\">شرح وظایف:</span></font></span></p></div><div style=\"text-align: right;\"><div style=\"\"><ul><li>&nbsp;پاسخگوئی و رسیدگی به مشکلات مالی و شهریه دانشجویان بصورت مراجعات حضوری و یا تماس تلفنی</li><li>آماده سازی سیستم رایانه ای شهریه جهت ثبت نام و انتخاب واحد در آغاز هر ترم تحصیلی</li><li>تنظیم جدول شهریه بر اساس آخرین بخشنامه سازمان</li><li>ثبت کمک شهریه سهم بنیاد شهید و امور ایثارگران برابر قرارداد فی مابین</li><li>ثبت کمک شهریه سهم سازمان بهزیستی برابر قرارداد فی مابین</li><li>ثبت تخفیفات شهریه برابر بخشنامه شامل تخفیفات ورزشی ، خانواده بیش از یک دانشجو ، رتبه علمی و ...</li><li>ثبت وام وزارت علوم طبق لیست ارسالی از صندوق رفاه دانشجوئی</li><li>بررسی پرونده مالی دانشجویان فارغ التحصیل ، انتقالی ، اخراجی و انصرافی در حداقل زمان و ارسال پرونده به دوایر مربوطه</li><li>استرداد طلب دانشجویان بستانکار پس از تسویه حساب نهائی و دریافت شماره حساب بانکی جهت واریز وجه طلب</li><li>صدور گواهی اعلام شهریه برای دانشجویان متقاضی</li></ul></div></div>');
INSERT INTO `menu` VALUES (43, 'امور مالی', 'اداری', '2ece248c-ff87-4091-8ebe-e4fa961a881e.jpg', '<div><div style=\"font-family: IRANSans;\"><table id=\"wfa7bf8a29cb477e62f7ac0ca1b870ad60\" class=\"table table-striped table-bordered detail-view\" style=\"width: 817.5px; margin-bottom: 20px; color: rgb(51, 51, 51); border-color: rgb(221, 221, 221); border-spacing: 0px; max-width: 100%; font-family: IRANSans, tahoma; font-size: 14px;\"><tbody><tr style=\"background-color: rgb(249, 249, 249);\"><th style=\"text-align: right; padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">تلفن</th><td style=\"padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">081-35455145</td></tr><tr><th style=\"text-align: right; padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">دورنگار</th><td style=\"padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">081-35345147</td></tr><tr style=\"background-color: rgb(249, 249, 249);\"><th style=\"text-align: right; padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">صندوق پستی</th><td style=\"padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">6553139771<br></td></tr></tbody></table></div><p dir=\"rtl\" style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px; font-family: IRANSans;\"><span style=\"font-weight: bolder;\"><font color=\"#333333\" face=\"IRANSans, tahoma\"><span style=\"font-size: 14px;\">شرح وظایف:</span></font></span></p></div><div>1- ‌ مشارکت در تصمیم گیری های مربوط به حل مشکلات و تعیین خط مشی دانشگاه از بعد مالی</div><div><br></div><div>2- نظارت بر اجرای صحیح کلیه مقررات، دستورالعمل‌ها و روش‌های مصوب مالی و معاملاتی.</div><div><br></div><div><br></div><div>3- بررسی و تایید نهایی پرداخت‌های دانشگاه با توجه به مجوزهای مالی</div><div><br></div><div><br></div><div>4- نظارت بر وضعیت نقدینگی و مشارکت در تصمیم‌گیری برای تامین منابع مالی مورد نیاز و چگونگی استفاده از منابع مالی موجود.</div><div><br></div><div><br></div><div>5- ایجاد، حذف یا تغییر در حساب‌های مالی به منظور اجراي هرچه مطلوب‌تر حسابداري تعهدي در ستاد و واحدهاي تابعه.</div><div><br></div><div><br></div><div>6- برنامه‌ریزی به منظور انطباق نظام‌های مالی و هزینه‌یابی&nbsp; با اقدامات جاري با توجه به لزوم محاسبه قيمت تمام شده فعاليت‌ها.</div><div><br></div><div><br></div><div>7- شركت در جلسات بودجه و همکاری لازم به منظور تهیه بودجه سالیانه دانشگاه و تايید آن.</div><div><br></div><div><br></div><div>8- برنامه‌ریزی و تصمیم‌گیری مقتضی در خصوص مغایرت عملکرد با بودجه ستاد و واحدهاي تابعه.</div><div><br></div><div><br></div><div>9- برنامه‌ریزی به جهت اجرای مصوبات هيات امناء درخصوص مسائل مالی و نظارت بر اجرای آن.</div><div><br></div><div><br></div><div>10- تهیه و تدوین دستورالعمل‌های تخصصی مالی و نظارت بر اجرای آن پس از تایید معاون پشتيباني و مشارکت در تهیه برنامه‌های سرمایه‌ای.</div><div><br></div><div><br></div><div>11- بررسی و تایید نهایی محاسبات مربوط به قیمت تمام شده خدمات و همچنین تهیه و ارائه گزارشات مدیریتي مورد نیاز دانشگاه.</div><div><br></div><div><br></div><div>12- تنظيم و تایید نهایی صورت‌های مالی ستاد و واحدهاي تابعه به تفكيك و تجميعي.</div><div><br></div><div><br></div><div>13- اعمال نظارت مالی بر ثبت، نگهداری و کنترل موجودي كالا و دارايی‌های .</div><div><br></div><div><br></div><div>14- برنامه ریزی جهت بستن حساب‌ها در پایان سال مالی و تهیه گزارش‌های مالی مورد نیاز جهت ارائه به مراجع ذی‌ربط.</div><div><br></div><div><br></div><div>15- نظارت عالی بر ثبت به موقع دفاتر رسمي و نظارت بر نگهداری اوراق و اسناد مرتبط با سرمایه‌گذاری‌ها، سهام، اسناد بهادار، اسناد تضمینی و نظایر آن.</div><div><br></div><div><br></div><div>16- هماهنگی امورمالی با سایر واحدهای دانشگاه از نظر مسائل مالی.</div><div><br></div><div><br></div><div>17- اتخاذ ترتیبات لازم به منظور هماهنگی با بازرس، حسابرسان و سایر مراجع ذی‌صلاح.</div><div><br></div><div><br></div><div>18- تعیین خط مشی و رویه‌ها به منظور رفع مغایرات با اشخاص و موسسات طرف حساب.</div><div><br></div><div><br></div><div>19- تصمیم‌گیری در خصوص تامین نیروی انسانی در واحدهای تحت نظارت.</div><div><br></div><div><br></div><div>20- هماهنگ نمودن عملیات واحدهای تحت نظارت و تصمیم‌گیری در خصوص نحوه تقسیم کار بین کارکنان تحت نظارت.</div><div><br></div><div><br></div><div>21- تنظیم برنامه آموزش جهت کارکنان امور مالی دانشگاه، به نحوی که کلیه کارکنان در جریان امور همکاران خود قرار گرفته و در موارد لزوم جایگزینی کارکنان به سهولت امکان پذیر گردد.</div><div><br></div><div><br></div><div>22- برنامه‌ریزی به منظور شمارش موجودی انبارها در پايان سال.</div><div><br></div><div><br></div><div>23- برنامه‌ریزی به منظور انجام امور مربوط به اصلاح حساب‌های سنوات قبل در مواقع مورد نیاز.&nbsp;</div><div><br></div><div>24- درخواست وجوه و كمكهاي مالي&nbsp; از خزانه معین استان به منظور ایجاد گردش مالی.</div><div><br></div><div><br></div><div>25- ارائه پیشنهاد به معاونت پشتيباني در خصوص تامین نیروی انسانی مورد نیاز مديريت امورمالي.</div><div><br></div><div><br></div><div>26- انجام سایر وظایف محوله از جانب معاون پشتيباني که با توجه به نوع وظایف، انجام آن توسط شخص دیگر امکان پذیر نباشد.</div>');
INSERT INTO `menu` VALUES (44, 'امور فناوری اطلاعات', 'اداری', '2ece248c-ff87-4091-8ebe-e4fa961a881e.jpg', '<div style=\"font-family: IRANSans;\"><div><table id=\"wfa7bf8a29cb477e62f7ac0ca1b870ad60\" class=\"table table-striped table-bordered detail-view\" style=\"width: 817.5px; margin-bottom: 20px; color: rgb(51, 51, 51); border-color: rgb(221, 221, 221); border-spacing: 0px; max-width: 100%; font-family: IRANSans, tahoma; font-size: 14px;\"><tbody><tr style=\"background-color: rgb(249, 249, 249);\"><th style=\"text-align: right; padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">تلفن</th><td style=\"padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">081-35455145</td></tr><tr><th style=\"text-align: right; padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">دورنگار</th><td style=\"padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">081-35345147</td></tr><tr style=\"background-color: rgb(249, 249, 249);\"><th style=\"text-align: right; padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">صندوق پستی</th><td style=\"padding: 8px; border-color: rgb(221, 221, 221); line-height: 1.42857;\">6553139771<br></td></tr></tbody></table></div><p dir=\"rtl\" style=\"margin-right: 0px; margin-bottom: 10px; margin-left: 0px;\"><span style=\"font-weight: bolder;\"><font color=\"#333333\" face=\"IRANSans, tahoma\"><span style=\"font-size: 14px;\">شرح وظایف:</span></font></span></p></div><div style=\"font-family: IRANSans; text-align: center;\"><div style=\"text-align: right;\"><div>1- تجهیز&nbsp; و بروز رسانی سامانه های اطلاعاتی دانشگاه نظیر سامانه آموزشی، پژوهشی، کتابخانه، تغذیه و سامانه های اداری- مالی</div><div><br></div><div>2-مدیریت شبکه های رایانه ای دانشکده ها و دانشگاه و فراهم نمودن ارتباط میان آنها</div><div><br></div><div>3- مسولیت ارتباطات اینترنتی و امور داده ای دانشگاه</div><div><br></div><div>4- مسولیت راه اندازی، نگهداری و توسعه Data Center دانشگاه</div><div><br></div><div>5- تجمیع داده های دانشگاه و ارائه سرویس هایی جهت دسترسی تحت شبکه به آنها</div><div><br></div><div>6- پشتیبانی سخت افزاری&nbsp; و نرم افزاری سیستم های دانشگاه</div><div><br></div><div>7- اجرا&nbsp; و ابلاغ سیاست های اعلام شده از جانب شورای فناوری اطلاعات دانشگاه</div></div></div>');
INSERT INTO `menu` VALUES (45, 'ارتباط با صنعت', 'اداری', '2ece248c-ff87-4091-8ebe-e4fa961a881e.jpg', '<p dir=\"RTL\" style=\"margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 14px; line-height: inherit; font-family: IranSans; vertical-align: baseline; color: rgb(51, 51, 51);\"><strong style=\"font-weight: bold; margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\">مهمترین اهداف دفتر ارتباط با صنعت دانشگاه به شرح زیر می‌باشد:</strong></p><ul><li><span style=\"font-family: inherit; font-size: inherit; font-style: inherit; font-variant-ligatures: inherit; font-variant-caps: inherit; font-weight: inherit; color: rgb(51, 51, 51);\">استفاده از توانایی ها و پتانسیل دانشگاه برای رفع نیازهای پژوهش و فناوری صنعت و جامعه و ارائه خدمات مشاوره ای به دستگاه های اجرایی و صنایع کشور</span></li><li><span style=\"font-family: inherit; font-size: inherit; font-style: inherit; font-variant-ligatures: inherit; font-variant-caps: inherit; font-weight: inherit; color: rgb(51, 51, 51);\">ایجاد زمینه های لازم جهت تولید و رشد علم و فناوری مورد نیاز کشور با انجام پژوهش های تقاضا محور، مشتری گرا و اثربخش در جامعه و صنعت با در نظرگیری اهداف ملی</span></li><li><span style=\"font-family: inherit; font-size: inherit; font-style: inherit; font-variant-ligatures: inherit; font-variant-caps: inherit; font-weight: inherit; color: rgb(51, 51, 51);\">ارتقاء کمی و کیفی ارتباط دانشگاه با مراکز اقتصادی و صنایع در سطوح بین‌المللی، ملی و استانی</span></li><li><span style=\"font-family: inherit; font-size: inherit; font-style: inherit; font-variant-ligatures: inherit; font-variant-caps: inherit; font-weight: inherit; color: rgb(51, 51, 51);\">قانونمند کردن نحوه ارتباط دانشگاهیان با مراکز خارج از دانشگاه</span></li></ul>');
INSERT INTO `menu` VALUES (46, 'طرح‌های عمرانی', 'اداری', '2ece248c-ff87-4091-8ebe-e4fa961a881e.jpg', 'در حال حاضر این مرکز فاقد بخش طرح های عمرانی می‌باشد');

-- ----------------------------
-- Table structure for news
-- ----------------------------
DROP TABLE IF EXISTS `news`;
CREATE TABLE `news`  (
  `Id` int(11) NOT NULL AUTO_INCREMENT,
  `Title` varchar(1000) CHARACTER SET utf8mb4 COLLATE utf8mb4_persian_ci NOT NULL,
  `Date` datetime NOT NULL,
  `News_Category_Id` int(11) NOT NULL,
  `Image` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_persian_ci NULL DEFAULT NULL,
  `Body` text CHARACTER SET utf8mb4 COLLATE utf8mb4_persian_ci NOT NULL,
  `Pics` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_persian_ci NULL DEFAULT NULL,
  PRIMARY KEY (`Id`) USING BTREE,
  INDEX `FK_News_News_Category`(`News_Category_Id`) USING BTREE,
  CONSTRAINT `FK_News_News_Category` FOREIGN KEY (`News_Category_Id`) REFERENCES `news_category` (`Id`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE = InnoDB AUTO_INCREMENT = 7 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_persian_ci ROW_FORMAT = Dynamic;

