INSERT alvanuastdb.news (Id, Title, Date, News_Category_Id, Image, Body, Pics) VALUES (3, N'انتخاب جناب آقای دکتر گنجی به عنوان مدیرعامل شرکت الوان ثابت', CAST(N'2022-01-07T22:10:00.000' AS DateTime), 1, N'2.jpg', N'انتخاب شایسته دکتر گنجی را به عنوان مدیرعامل شرکت الوان ثابت تبریک عرض می نمائیم.
از درگاه خداوند منان توفیق روزافزون ایشان را آرزومندیم.

" دانشگاه علمی‌کاربردی مرکز آموزش الوان ثابت"
