INSERT alvanuastdb.news (Id, Title, Date, News_Category_Id, Image, Body, Pics) VALUES (7, N'برگزاری جشن نودانشجویان دانشگاه جامع علمی-کاربردی استان همدان', CAST(N'2021-11-10T05:40:00.000' AS DateTime), 2, N'5.jpg', N'به اطلاع میرساند مراسم دانشجویان جدیدالورود در  روز چهارشنبه مورخ ۱۴۰۰/۰۸/۱۹ ساعت ۱۰:۳۰ با حضور دکتر عباسی معاون محترم فرهنگی و دانشجویی برگزار میگردد.
لذا از تمامی دانشجویان عزیز دعوت بعمل می آید که در این مراسم شرکت نمایند. 
توجه نمایید جهت شرکت در مراسم بصورت مجازی بر اساس آدرسهای مندرج در پوستر👇اقدام نمایید.
حضور تمام دانشجویان علی الخصوص دانشجویان ورودی ۴۰۰ در این مراسم الزامی میباشد.', NULL)
;





